import "./CostItem.css";

function CostItem(props) {
  const costDate = props.date;
  const costDescription = props.description;
  const costAmmount = props.ammount;

  return (
    <div className="cost-item">
      <div>
        <div>{costDate.toLocaleString("ru-RU", { month: "long" })}</div>
        <div>{costDate.getFull.Year()}</div>
      </div>

      <div className="cost-item__description">
        <h2>{costDescription}</h2>
        <div className="cost-item__price">{costAmmount}</div>
      </div>
    </div>
  );
}
export default CostItem;
