  const [btn_theme, setBtn_theme] = useState("");

  const currentTheme = useContext(ThemeContext);
  console.log(`${currentTheme.theme}`);
  useEffect(() => {});
  setBtn_theme(currentTheme.theme);
  return (
    <div>
      <button className="btn_1 ${currentTheme.theme} ">
        {props.innerText}
      </button>
    </div>
  );
}

export default Button_1;
