import "./Header.css";
function Header(props) {
  const { s1, s, setS, inputW, setInputW } = props;

  function handleClick_6() {
    console.log(s);
    console.log(setS);
    //for (let i = 0; i < s1.length; i++) {
    //  s1[i].deleted = false;
    //}

    setTimeout(() => {
      s1.forEach((el) => {
        el.deleted = false;
      });
    }, 0);

    setTimeout(() => {
      console.log(s);
      setS([...s1]);
      localStorage.setItem("liked_0987654321", JSON.stringify(s1));
    }, 0);
  }

  function handleClick_7() {
    setInputW(true);
    console.log(inputW);
  }

  return (
    <div className="header_wrapper">
      <div className="Strany">Путеводитель по странам мира </div>
      <div className="right_menu">
        <div className="Vernut">
          <button
            className="btn"
            onClick={() => {
              handleClick_7();
            }}
          >
            Отобрать по частям света
          </button>
        </div>

        <div className="Vernut">
          <button
            className="btn"
            onClick={() => {
              handleClick_6();
            }}
          >
            Вернуть все удаленные
          </button>
        </div>
      </div>
    </div>
  );
}
export default Header;
