import "./Container.css";
import { useState, useEffect } from "react";
import Post from "../Post/Post";

function Container() {
  const [posts, setPosts] = useState([]);

  const [current, setCurrent] = useState([]);
  let storage1 = "";
  let storage = [];

  // let storage = localStorage.getItem("storage1").JSON.parse;

  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((json) => setPosts(json));
  }, []);
  //console.log(posts);
  for (let i = 0; i < posts.length; i++) {
    let current_el = {
      id: i,
      name: posts[i].translations.rus.common,
      continent: posts[i].continents,
      capital: posts[i].capital,
      population: posts[i].population,
      flag: posts[i].flags.svg,
      map: posts[i].maps.googleMaps,
      isLiked: storage[i].isLiked,
      isDeleted: storage[i].isDeleted,
      note: storage[i].note,
    };
    current[i] = current_el;
  }
  setCurrent(current);
  //  console.log(s);
  return (
    <div className="main">
      {posts.map((el) => (
        <Post
          name={el.name.common}
          rus={el.translations.rus.common}
          continent={el.continents}
          capital={el.capital}
          population={el.population}
          flag={el.flags.svg}
          map={el.maps.googleMaps}
        />
      ))}
    </div>
  );
}

export default Container;
