import "./InputW.css";
function InputW(props) {
  const { inputW, setInputW, s, setS, s1 } = props;
  let s3 = [];

  function handleClick_7() {
    setInputW(false);
  }
  function formSubmit(e) {
    e.preventDefault();
    console.log(e.target.Eu.checked);
    s3 = structuredClone(s1);
    s3.forEach((el) => {
      console.log(el.continent[0]);
      console.log(e.target.Eu.checked);

      if (el.continent[0] == "Europe" && e.target.Eu.checked == false) {
        el.deleted = true;
        console.log(el.deleted);
      }
      if (el.continent[0] == "Europe" && e.target.Eu.checked == true) {
        el.deleted = false;
      }

      if (el.continent == "Asia" && e.target.As.checked == false) {
        el.deleted = true;
      }
      if (el.continent == "Asia" && e.target.As.checked == true) {
        el.deleted = false;
      }

      if (el.continent == "North America" && e.target.NA.checked == false) {
        el.deleted = true;
      }
      if (el.continent == "North America" && e.target.NA.checked == true) {
        el.deleted = false;
      }

      if (el.continent == "South America" && e.target.SA.checked == false) {
        el.deleted = true;
      }
      if (el.continent == "South America" && e.target.SA.checked == true) {
        el.deleted = false;
      }

      if (el.continent == "Oceania" && e.target.Oc.checked == false) {
        el.deleted = true;
      }
      if (el.continent == "Oceania" && e.target.Oc.checked == true) {
        el.deleted = false;
      }

      if (el.continent == "Antarctica" && e.target.An.checked == false) {
        el.deleted = true;
      }
      if (el.continent == "Antarctica" && e.target.An.checked == true) {
        el.deleted = false;
      }

      if (el.continent == "Africa" && e.target.Af.checked == false) {
        el.deleted = true;
      }
      if (el.continent == "Africa" && e.target.Af.checked == true) {
        el.deleted = false;
      }
    });
    setTimeout(() => {
      console.log(s3);
      setS([...s3]);
      localStorage.setItem("liked_0987654321", JSON.stringify(s3));
    }, 1000);

    setInputW(false);
  }

  if (inputW == true) {
    return (
      <div className="container">
        <div className="inputW">
          <div
            className="cross"
            onClick={() => {
              handleClick_7();
            }}
          >
            X
          </div>
          <div className="header_input">
            Отметьте галочками то, <br />
            что хотите отобразить:
          </div>
          <form className="form" onSubmit={formSubmit}>
            <label>
              Европа:
              <input type="checkbox" name="Eu" />
            </label>
            <br />
            <label>
              Азия:
              <input type="checkbox" name="As" />
            </label>
            <br />
            <label>
              Северная Америка:
              <input type="checkbox" name="NA" />
            </label>
            <br />
            <label>
              Южная Америка:
              <input type="checkbox" name="SA" />
            </label>
            <br />
            <label>
              Oceania:
              <input type="checkbox" name="Oc" />
            </label>
            <br />
            <label>
              Антарктика:
              <input type="checkbox" name="An" />
            </label>
            <br />
            <label>
              Африка:
              <input type="checkbox" name="Af" />
            </label>
            <br />
            <br />
            <button class="btn1" type="submit">
              подтвердить выбор
            </button>
          </form>
        </div>
      </div>
    );
  }
}
export default InputW;
