import "./RoundKnob.css";
import portfolioContent from "../Data/data";
import { ThemeContext } from "../App";
import { useContext } from "react";

function RoundKnobL(props) {
  const { i, sc } = props;
  const currentTheme = useContext(ThemeContext);
  function handleClick(i) {
    if (i == 0) {
      sc(portfolioContent.length - 1);
    } else {
      sc(i - 1);
    }
  }

  return (
    <div>
      {currentTheme.theme == "dark" && (
        <button
          className="round_dark"
          onClick={() => {
            handleClick(i);
          }}
        >
          {props.children}
        </button>
      )}
      {currentTheme.theme == "light" && (
        <button
          className="round_light"
          onClick={() => {
            handleClick(i);
          }}
        >
          {props.children}
        </button>
      )}
    </div>
  );
}

export default RoundKnobL;
