import "./Container.css";
import Button_1 from "./Button_1.js";
import "./Button_1.css";
import PhotoZone from "./PhotoZone.js";
import Card1 from "./Card1.js";
import BlueArrow from "./BlueArrow.js";
import Card2 from "./Card2.js";
import portfolioContent from "../Data/data.js";
import LetsWork from "./LetsWork/LetsWork.js";
import FB_VK from "./FB_VK/FB_VK.js";
import { useContext } from "react";
import { ThemeContext } from "../App.js";
import "../App.css";
import { useState } from "react";

function Container(props) {
  const i = props.i;
  const sc = props.sc;
  const currentTheme = useContext(ThemeContext);

  function handleClick_1() {
    if (currentTheme.theme === "dark") {
      currentTheme.setTheme("light");
    } else {
      currentTheme.setTheme("dark");
    }
  }
  return (
    <div className="Container" id="Home">
      <header>
        <div className="spacing0"></div>
        <div className="navMenu1">
          <div className="Denis">Denis</div>
          <div className="buttons1">
            <Button_1 innerText={"About me"} url={"About"} />
            <Button_1 innerText={"PortFolio"} url={"Portfolio"} />
            <Button_1 innerText={"Contact me"} url={"About"} />
          </div>
          <div
            onClick={() => {
              handleClick_1();
            }}
          >
            <Button_1 innerText={`switch theme`} url={"Home"} />
          </div>
        </div>
        <div className="Wrapper_1">
          <div className="Stunning_Programmer_Wrapper   ">
            <div className="Stunning_Programmer      ">Frontend Developer</div>

            <div className="Denis_Chigvintsev1            ">
              Denis Chigvintsev
            </div>
          </div>
          <div className="central_1"></div>
          <LetsWork />
        </div>
        <div className="divider1      "></div>
      </header>
      <main>
        <section>
          <div>
            <PhotoZone />
          </div>
        </section>
        <div className="spacing1"></div>
        <section id="About">
          <Card1 theme={currentTheme.theme} />
        </section>

        <section id="Portfolio">
          <Card2
            title={portfolioContent[i].title}
            scope={portfolioContent[i].scope}
            url={portfolioContent[i].url}
            i={i}
            sc={sc}
          />
        </section>
      </main>
      <div className="divider1 divider2"></div>
      <footer>
        <div className="footer">
          <div>
            <LetsWork />
          </div>
          <div>
            <div className="Denis_Chigvintsev1">Denis Chigvintsev</div>
            <div className="footer_fb">
              <FB_VK />
            </div>
          </div>
        </div>

        <div className="divider1 divider3"></div>
        <div className="cr        ">&copy; 2024</div>
      </footer>
    </div>
  );
}

export default Container;
