import "./Modal.css";
import { ThemeContext } from "../../App";
import { useContext } from "react";

function Modal(props) {
  const { isModal, setIsModal } = props;
  const currentTheme = useContext(ThemeContext);
  let switch1 = -1;
  if (currentTheme.theme == "dark" && isModal == true) {
    switch1 = 0;
  }
  if (currentTheme.theme == "light" && isModal == true) {
    switch1 = 1;
  }
  console.log(switch1);

  function handleClick_2() {
    setIsModal(false);
  }

  if (switch1 === 1) {
    return (
      <div className="container">
        <div className="modal_light">
          <div
            className="cross_light"
            onClick={() => {
              handleClick_2();
            }}
          >
            X
          </div>
          <br />
          <div className="resume">DENIS CHIGVINTSEV</div>
          <br />
          <div></div>
        </div>
      </div>
    );
  }
  if (switch1 === 0) {
    return (
      <div className="container">
        <div className="modal_dark">
          <div
            className="cross_dark"
            onClick={() => {
              handleClick_2();
            }}
          >
            X
          </div>
          <br />
          <div className="resume">DENIS CHIGVINTSEV</div>
          <br />
          <div></div>
        </div>
      </div>
    );
  }
}

export default Modal;
