import "./Container.css";
import { useState, useEffect } from "react";
import Post from "../Post/Post";

let els = {};
let s1 = [];
let els1 = {};
let s2 = [];

function Container(props) {
  const [posts, setPosts] = useState([]);
  let { s, setS } = props;
  let locS;
  let deleted;
  let liked;
  let locS_l;

  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((json) => setPosts(json));

    console.log(locS);
  }, []);
  //console.log(posts);

  locS = JSON.parse(localStorage.getItem("deleted_0987654321"));
  locS_l = JSON.parse(localStorage.getItem("liked_0987654321"));

  console.log(locS);

  for (let i = 0; i < posts.length; i++) {
    if (!locS) {
      deleted = false;
    } else {
      deleted = locS[i].deleted;
    }
    if (!locS_l) {
      liked = false;
    } else {
      liked = locS_l[i].liked;
    }

    els = {
      id: i,
      name: posts[i].name.common,
      rus: posts[i].translations.rus.common,
      continent: posts[i].continents,
      capital: posts[i].capital,
      population: posts[i].population,
      flag: posts[i].flags.svg,
      map: posts[i].maps.googleMaps,
      deleted: deleted,
      liked: liked,
    };
    els1 = { deleted: deleted, liked: liked };
    s1[i] = els;
    s2[i] = els1;
  }
  setTimeout(() => {
    console.log(123);
    setS(s1);
    //localStorage.setItem("countries", JSON.stringify(s));
  }, 0);
  return (
    <div>
      <header>
        <div className="header_wrapper">
          <div className="Strany">Путеводитель по странам мира</div>

          <div className="Vernut">
            <button> Вернуть все удаленные карточки </button>
          </div>
        </div>
        <br />
      </header>

      <div className="main">
        {s.map((el) => (
          <Post
            id={el.id}
            name={el.name}
            rus={el.rus}
            continent={el.continent}
            capital={el.capital}
            population={el.population}
            flag={el.flag}
            map={el.map}
            s1={s1}
            s={s}
            setS={setS}
          />
        ))}
      </div>
    </div>
  );
}
export default Container;
