import "./Container.css";
import { useState, useEffect } from "react";
import Post from "../Post/Post";

let els = {};

function Container(props) {
  const [posts, setPosts] = useState([]);
  let { s, setS } = props;
  let locS;
  let deleted;

  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((json) => setPosts(json));
    locS = localStorage.getItem("countries");
  }, []);
  //console.log(posts);
  for (let i = 0; i < posts.length; i++) {
    if (!locS) {
      deleted = "false";
    } else {
      deleted = loc[i].deleted;
    }

    els = {
      id: i,
      name: posts[i].name.common,
      rus: posts[i].translations.rus.common,
      continent: posts[i].continents,
      capital: posts[i].capital,
      population: posts[i].population,
      flag: posts[i].flags.svg,
      map: posts[i].maps.googleMaps,
    };

    s[i] = els;
  }
  setTimeout(() => setS(s), 0);
  return (
    <div className="main">
      {posts.map((el) => (
        <Post
          name={el.name.common}
          rus={el.translations.rus.common}
          continent={el.continents}
          capital={el.capital}
          population={el.population}
          flag={el.flags.svg}
          map={el.maps.googleMaps}
        />
      ))}
    </div>
  );
}
export default Container;
