import "./Container.css";
import { useState, useEffect } from "react";
import Post from "../Post/Post";

let els = {};
let els1 = {};

function Container(props) {
  let { s, setS, posts, setPosts } = props;

  let s1 = [];
  let s2 = [];
  s2 = JSON.parse(localStorage.getItem("countries"));
  //console.log(s2);

  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((json) => setPosts(json));

    for (let i = 0; i < posts.length; i++) {
      els = {
        id: i,
        name: posts[i].name.common,
        rus: posts[i].translations.rus.common,
        continent: posts[i].continents,
        capital: posts[i].capital,
        population: posts[i].population,
        flag: posts[i].flags.svg,
        map: posts[i].maps.googleMaps,
        liked: s2[i].liked,
        deleted: s2[i].deleted,
        note: s2[i].note,
      };
      els1 = {
        liked: s2[i].liked,
        deleted: s2[i].deleted,
        note: s2[i].note,
      };
      s1[i] = els;
      s2[i] = els1;
    }
  }, []);
  //console.log(posts);

  for (let j = 0; j < posts.length; j++) {
    els1 = {
      liked: s[j].liked,
      deleted: s[j].deleted,
      note: s[j].note,
    };

    s2[j] = els1;
  }

  localStorage.setItem("countries", JSON.stringify(s2));
  setTimeout(() => setS(s1), 0);

  return (
    <div className="main">
      {s.map((el) => (
        <Post
          name={el.name}
          rus={el.rus}
          continent={el.continent}
          capital={el.capital}
          population={el.population}
          flag={el.flag}
          map={el.map}
          liked={el.liked}
          deleted={el.deleted}
          note={el.note}
          s={s}
          setS={setS}
          s1={s1}
          id={el.id}
        />
      ))}
    </div>
  );
}
export default Container;
