import style from "./Modal.module.css";

async function Modal(props) {
  const { isModal, setIsModal } = props;

  try {
    const res = await fetch(
      " http://anecdotica.ru/api?pid=O7DVZbtc408q9H7V4x2F&method=getRandItem&format=xml&charset=cp1251&markup=1&token=0123456789abcdeABCDE    "
    );
    const res1 = res.jason();
  } catch (error) {}

  function handleClick_2() {
    setIsModal(false);
  }

  return (
    isModal == true && (
      <div className={style.container}>
        <div className={style.modal_light}>
          <div
            className={style.cross_light}
            onClick={() => {
              handleClick_2();
            }}
          >
            X
          </div>
          <br />
          <div className={style.resume}>DENIS CHIGVINTSEV</div>
          <br />
          <div>{res1}</div>
        </div>
      </div>
    )
  );
}

export default Modal;
