import "./Menu.css";
import like from "../../img/like.svg";
import delete1 from "../../img/delete.svg";

function Menu(props) {
  const { isMenu, setIsMenu, s, setS, s1 } = props;
  //console.log(isMenu);

  function handleClick_2() {
    setIsMenu(false);
  }

  function handleClick_3() {
    let i = s1.id;
    s1[i].liked = true;
    setS(s1);
    setIsMenu(false);
    console.log(s[i].liked);
  }

  if (isMenu == true) {
    return (
      <del
        className="menu_wrapper"
        onClick={() => {
          handleClick_2();
        }}
      >
        <div
          className="el_menu"
          onClick={() => {
            handleClick_3();
          }}
        >
          Добавить в избранное &#10084;{" "}
        </div>
        <div className="el_menu">Удалить из представления &#128465; </div>
        <div className="el_menu">Заметка</div>
        <div className="el_menu">Закрыть меню X </div>
      </del>
    );
  }
}

export default Menu;
