import "./Menu.css";
import like from "../../img/like.svg";
import delete1 from "../../img/delete.svg";

function Menu(props) {
  const { isMenu, setIsMenu, s1, setS, id, s } = props;
  //console.log(isMenu);

  function handleClick_2() {
    setIsMenu(false);
  }
  function handleClick_3() {
    s1[id].deleted = true;
    setS(s1);

    setTimeout(() => {
      localStorage.setItem("deleted_0987654321", JSON.stringify(s));
      console.log(s[id].deleted);
    }, 0);
  }
  function handleClick_4() {
    s1[id].liked = true;
    setS(s1);

    setTimeout(() => {
      localStorage.setItem("liked_0987654321", JSON.stringify(s));
      console.log(s[id].liked);
    }, 0);
  }

  if (isMenu == true) {
    return (
      <del
        className="menu_wrapper"
        onClick={() => {
          handleClick_2();
        }}
      >
        <div
          className="el_menu"
          onClick={() => {
            handleClick_4();
          }}
        >
          Добавить в избранное &#10084;{" "}
        </div>
        <div
          className="el_menu"
          onClick={() => {
            handleClick_3();
          }}
        >
          Удалить из представления &#128465;{" "}
        </div>
        <div className="el_menu">Заметка</div>
        <div className="el_menu">Закрыть меню X </div>
      </del>
    );
  }
}

export default Menu;
