console.log(data);

function Container() {
  //const [posts, setPosts] = useState(data);

  async function getPosts() {
    try {
      const res = await fetch("https://restcountries.com/v3.1/all");
      data = await res.json();
    } catch (error) {
      console.err(error);
    }

    //  setPosts(data);
  }

  getPosts();

  console.log(data);

