import "./Container.css";
import { useState, useEffect } from "react";
import Post from "../Post/Post";

let els = {};
let els1 = {};

function Container() {
  const [posts, setPosts] = useState([]);
  let [s, setS] = useState([]);
  let s1 = [];

  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((json) => setPosts(json));
  }, []);
  //console.log(posts);
  s1 = JSON.parse(localStorage.getItem("countries"));
  for (let i = 0; i < posts.length; i++) {
    if (!els1) {
      els1 = {
        liked: false,
        deleted: false,
        note: "",
      };
    }

    els = {
      id: i,
      name: posts[i].name.common,
      rus: posts[i].translations.rus.common,
      continent: posts[i].continents,
      capital: posts[i].capital,
      population: posts[i].population,
      flag: posts[i].flags.svg,
      map: posts[i].maps.googleMaps,
      liked: s1.liked,
      deleted: s1.deleted,
      note: s1.note,
    };

    s[i] = els;
    s1[i] = els1;
    localStorage.setItem("countries", JSON.stringify(els1));
  }

  setTimeout(() => setS(s), 0);

  return (
    <div className="main">
      {s.map((el) => (
        <Post
          name={el.name}
          rus={el.rus}
          continent={el.continent}
          capital={el.capital}
          population={el.population}
          flag={el.flag}
          map={el.map}
        />
      ))}
    </div>
  );
}
export default Container;
