      console.log(s);
    }, 0);
    localStorage.setItem("deleted_0987654321", JSON.stringify(s1));
    setTimeout(() => {}, 0);
  }

  return (
    <div className="header_wrapper">
      <div className="Strany">Путеводитель по странам мира</div>

      <div className="Vernut">
        <button
          className="btn"
          onClick={() => {
            handleClick_6();
          }}
        >
          Вернуть все <br /> удаленные карточки
        </button>
      </div>
    </div>
  );
}
export default Header;
