import "./Post.css";
function Post(props) {
  let { name, rus, continent, capital, population, flag, map } = props;
  console.log(continent);
  /*switch (continent) {
    case " Europe":
      continent = "Европа";
      break;
    case "Africa":
      continent = "Африка";
      break;
    case "Antarctica":
      continent = "Антарктика";
      break;
    case "Asia":
      continent = "Азия";
      break;
    case "North America":
      continent = "Северная Америка";
      break;
    case "South America":
      continent = "Южная Америка";
      break;
    default: {
    }
  }
*/
  return (
    <div className="post">
      <div className="margin">
        <div>{rus}</div>
        <br />
        <div>Столица: {capital}</div>
        <div>{continent}</div>
        <div>{population}</div>

        <div>
          <img className="flag" src={flag} />
        </div>
        <div>
          <a href={map} target="_blank">
            Карта
          </a>
        </div>
      </div>
    </div>
  );
}
export default Post;
