import "./Post.css";
import burger_img from "../../img/burger_grey.svg";
import { useState } from "react";
import Menu from "../Menu/Menu";

function Post(props) {
  const [isMenu, setIsMenu] = useState(false);
  let style1;

  function handleClick() {
    setIsMenu(true);
    console.log(isMenu);
  }

  let {
    id,
    name,
    rus,
    continent,
    capital,
    population,
    flag,
    map,
    s1,
    s,
    setS,
  } = props;
  if (continent == "Europe") {
    continent = "Европа";
  }
  if (continent == "South America") {
    continent = "Южная Америка";
  }
  if (continent == "North America") {
    continent = "Северная Америка";
  }
  if (continent == "Antarctica") {
    continent = "Антарктика";
  }
  if (continent == "Asia") {
    continent = "Азия";
  }
  if (continent == "Africa") {
    continent = "Африка";
  }

  if (s[id].liked == true) {
    style1 = "post1";
  } else {
    style1 = "post2";
  }

  if (s[id].deleted == false) {
    return (
      <div className={style1}>
        <div className="menu_position">
          <Menu
            isMenu={isMenu}
            setIsMenu={setIsMenu}
            s1={s1}
            setS={setS}
            id={id}
            s={s}
          />
        </div>
        <div className="Burger_wrapper">
          <img
            className="Burger"
            onClick={() => {
              handleClick();
            }}
            src={burger_img}
          />
        </div>

        <div className="margin">
          <div>{rus}</div>
          <br />
          <div>Столица: {capital}</div>
          <div>Часть света: {continent}</div>
          <div className="population">Население: {population} чел.</div>
          <br />
          <div>
            <img
              className="flag"
              src={flag}
              alt="флаг"
              loading="lazy"
              decoding="asynchronous"
            />
          </div>
          <div>
            <a href={map} target="_blank" className="map">
              Карта
            </a>
          </div>
        </div>
      </div>
    );
  }
}
export default Post;
