import style from "./Modal.module.css";
import { useContext } from "react";
import { ThemeContext } from "../../App";

function Modal(props) {
  const { isModal, setIsModal } = props;
  const currentTheme = useContext(ThemeContext);

  function handleClick_2() {
    setIsModal(false);
  }
  console.log(isModal, currentTheme.theme);
  if (currentTheme.theme == "light") {
    const style1 = "style.modal_light";
    const style2 = "style.cross_light";
  } else {
    const style1 = "style.modal_dark";
    const style2 = "style.cross_dark";
  }
  return (
    isModal == true && currentTheme.theme == "light" && (
      <div className={style.container}>
        <div className={style.modal_light}>
          <div
            className={style.cross_light}
            onClick={() => {
              handleClick_2();
            }}
          >
            X
          </div>
        </div>
      </div>
    ),
    isModal == true && currentTheme.theme == "dark" && (
      <div className={style.container}>
        <div className={style.modal_dark}>
          <div
            className={style.cross_dark}
            onClick={() => {
              handleClick_2();
            }}
          >
            X
          </div>
        </div>
      </div>
    )
  );
}

export default Modal;
