import "./BlueArrow.css";
import BlueImg from "../img/BlueArrow.svg";
function BlueArrow(props) {
  const url = props.url;
  function blue_click() {
    document.getElementById(`${url}`).scrollIntoView({ behavior: "smooth" });
  }
  return (
    <button
      className="ButtonBlue"
      onClick={() => {
        blue_click();
      }}
    >
      <img className="imgBlue" src={BlueImg} />
    </button>
  );
}
export default BlueArrow;
