import "./Post.css";
import burger_img from "../../img/burger_grey.svg";
import { useState } from "react";
import Menu from "../Menu/Menu";

function Post(props) {
  let [menu, setIsMenu] = useState(false);

  function handleClick() {
    if ((menu = false)) {
      setIsMenu(true);
    } else {
      setIsMenu(false);
    }
  }

  let { name, rus, continent, capital, population, flag, map } = props;
  if (continent == "Europe") {
    continent = "Европа";
  }
  if (continent == "South America") {
    continent = "Южная Америка";
  }
  if (continent == "North America") {
    continent = "Северная Америка";
  }
  if (continent == "Antarctica") {
    continent = "Антарктика";
  }
  if (continent == "Asia") {
    continent = "Азия";
  }
  if (continent == "Africa") {
    continent = "Африка";
  }

  return (
    <div className="post">
      <div className="Burger_wrapper">
        <img
          className="Burger"
          onClick={() => {
            handleClick();
          }}
          src={burger_img}
        />
      </div>
      {menu == "true" && <Menu />}
      <div className="margin">
        <div>{rus}</div>
        <br />
        <div>Столица: {capital}</div>
        <div>Часть света: {continent}</div>
        <div className="population">Население: {population} чел.</div>
        <br />
        <div>
          <img className="flag" src={flag} />
        </div>
        <div>
          <a href={map} target="_blank">
            Карта
          </a>
        </div>
      </div>
    </div>
  );
}
export default Post;
