import "./Card1.css";
import img_2 from "../img/2.jpg";

import Button_1 from "./Button_1";
import RoundKnob from "./RoundKnobL";
import FB_VK from "./FB_VK/FB_VK";
import { useState } from "react";
import Modal from "./Modal/Modal";
function Card1() {
  const [isModal, setIsModal] = useState(false);
  function handleClick_1() {
    setIsModal(true);
  }
  return (
    <div className="Card1">
      <div className="HeaderC1">
        <div className="IamDenis">I am Denis</div>{" "}
        <Button_1 innerText={"Home"} url={"Home"} />
      </div>

      <div className="SpacingC1"></div>
      <div className="CardMain">
        <div className="CardInd1">
          <img className="imgCard1" src={img_2} />
        </div>
        <div className="CardInd2">
          <div className="Introduction">Introduction</div>
          <div className="SpacingC2"></div>
          <p>
            My journey as a frontend developer started in July 2024 with my
            joing the Urban University stunning web development study program.
            Since then I have found inspiration in every bit of this diverse and
            vibrant field. I have already made quite a few frontend projects and
            would like to mention some of them herebelow.
          </p>
          <div className="SpacingC3"></div>
          <div className="Introduction">Contact Information</div>
          <div className="SpacingC4"></div>
          <div className="contact_container">
            <div className="Email">
              Email: <div>chigvin@OrionLLC.ru</div>
            </div>
            <div className="Phone">
              Phone number: <div>+7 902 839 44 06</div>{" "}
            </div>
          </div>
          <div className="SpacingC5"></div>
          <div className="set_of_socials">
            <FB_VK />
            <div className="two_buttons">
              <div
                onClick={() => {
                  handleClick_1();
                }}
              >
                <Button_1 innerText={"My Resume"} url={"About"} />
              </div>
            </div>
          </div>
        </div>
      </div>
      <Modal isModal={isModal} setIsModal={setIsModal} />
    </div>
  );
}
export default Card1;
