import "./Container.css";
import { useState, useEffect } from "react";
import Post from "../Post/Post";
import { isUtf8 } from "buffer";
//let s = [];
const fs = require("fs");

function Container() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((json) => {
        const res = JSON.stringify(json);
        fs.writeFileSync("./Data/data.txt", res);
        const res4 = fs.readFileSync("./Data/data.txt", "utf8");
        setPosts(JSON.parse(res4));
      });
  }, []);
  //console.log(posts);
  //for (let i = 0; i < posts.length; i++) {
  //  s.push(posts[i].name.common);
  // s[i].id = i + 1;
  //}
  //alert(s);

  return (
    <div className="main">
      {posts.map((el) => (
        <Post
          name={el.name.common}
          rus={el.translations.rus.common}
          continent={el.continents}
          capital={el.capital}
          population={el.population}
          flag={el.flags.svg}
          map={el.maps.openStreetMaps}
        />
      ))}
    </div>
  );
}
export default Container;
