import "./Container.css";
import { useState, useEffect } from "react";
import Post from "../Post/Post";

let els = {};
let s1 = [];
let els1 = {};
let s2 = [];

function Container(props) {
  const [posts, setPosts] = useState([]);
  let { s, setS } = props;
  let locS;
  let deleted;
  let liked;

  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all")
      .then((response) => response.json())
      .then((json) => setPosts(json));
    locS = JSON.parse(localStorage.getItem("countries"));
    console.log(locS);
  }, []);
  //console.log(posts);
  setTimeout(() => {
    for (let i = 0; i < posts.length; i++) {
      if (!locS) {
        deleted = false;
      } else {
        deleted = locS[i].deleted;
      }

      els = {
        id: i,
        name: posts[i].name.common,
        rus: posts[i].translations.rus.common,
        continent: posts[i].continents,
        capital: posts[i].capital,
        population: posts[i].population,
        flag: posts[i].flags.svg,
        map: posts[i].maps.googleMaps,
        deleted: deleted,
      };
      els1 = { deleted: deleted };
      s1[i] = els;
      s2[i] = els1;
    }
  }, 1000);
  setTimeout(() => {
    console.log(123);
    setS(s1);
    localStorage.setItem("countries", JSON.stringify(s));
  }, 0);
  return (
    <div className="main">
      {s.map((el) => (
        <Post
          id={el.id}
          name={el.name}
          rus={el.rus}
          continent={el.continent}
          capital={el.capital}
          population={el.population}
          flag={el.flag}
          map={el.map}
          s1={s1}
          s={s}
          setS={setS}
        />
      ))}
    </div>
  );
}
export default Container;
